import React, { Component, Fragment } from 'react'
import { connect } from 'react-redux'
import 'materialize-css';
import { Select, Button, Icon } from 'react-materialize';
import '../css/login.css'
// bring in action to set authed user
import { setCurrentUser } from '../actions/authedUser'

class LogIn extends Component {
    state = {
        authedId: ''
    }

    handleChange = (e) => {
        const authedId = e.target.value
        console.log(authedId)

        this.setState(() => ({
            authedId
        }))
    } 
    
    handleSubmit = (e) => {
        e.preventDefault()

        const { authedId } = this.state

        const { dispatch } = this.props

        dispatch(setCurrentUser(authedId))

        this.setState(() => ({
            authedId: ''
        }))
    }

    render() {
        const { userList } = this.props
        
        return (
            <Fragment>
                <header>
                    <h1 className="login-title">Would You Rather?</h1>
                </header>

                <div className='main-content'>
                    <section>
                        <img 
                            src="data:image/jpeg;base64," 
                            alt="welcome"
                            className="welcome-image"
                        />
                    </section>
                    <section className="logIn">
                        <h4 className='login-header'>Log in and join the fun !</h4>
                        <div className='login-info'>
                            <Select 
                              id="user-select"
                              multiple={false}
                              onChange={this.handleChange}
                              options={{
                                  closeOnClick: true,
                              }}
                              value=''>
                                <option disabled value=''>Select a User</option>
                                {userList.map((user) => (
                                     <option key={user.id} value={user.id}>{user.name}</option>
                                ))}
                            </Select>
                        </div>
                        <Button 
                          node="button"
                          type="submit"
                          onClick={this.handleSubmit}>Log In!
                            <Icon right>send</Icon>
                        </Button>
                    </section>
                    
                </div>
            </Fragment>
        )
    }
}

function mapStateToProps ({ users, authedUser }) {
    const userList = Object.entries(users).map(user => {
        return {
            id: user[1].id,
            name: user[1].name
        }
    })
    console.log('user list', userList)
    return {
        userList,
        authedUser
    }
}

export default connect(mapStateToProps)(LogIn)